INSERT INTO `maintenance` (`RegID`, `Name`, `Email`, `Campus`, `RoomNumber`, `Priority`, `ProblemType`, `Status`, `Description`, `Assigned`, `TimeStart`, `TimeStop`, `Resolution`) VALUES (416, 'Beth Bonk', 'jringenberg2@peoriachristian.org', 'California', '24', 'Medium (Needs attention soon)', 'Plumbing', 'Resolved (Completed)', 'This is about the North Lounge womens restroom.  I really think the hot water faucet is leaking.  I noticed that when I shut it off yesterday, water was pooling to the left of the faucet and running onto the carpet.  Now maybe we are just being really messy, but I think you might want to take a look at it.  Thanks!!', '---SELECT ONE---', '2006-08-30', '2006-08-29', 'I dried off the water, ran the faucet, no leak, possibly the reason for the water is that the towel dispenser is right over the corner of the sink.');
INSERT INTO `maintenance` (`RegID`, `Name`, `Email`, `Campus`, `RoomNumber`, `Priority`, `ProblemType`, `Status`, `Description`, `Assigned`, `TimeStart`, `TimeStop`, `Resolution`) VALUES (417, 'Beth Bonk', 'jringenberg2@peoriachristian.org', 'California', '16 I', 'Medium (Needs attention soon)', 'Other', 'In Progress (See Resolution Comments)', 'eHi again.  We have a light out over Jeans desk.  Also, there is a ceiling tile popped out over our sink.  We dont know why.  Its next to a light that is a different color (yellow) from the rest of our lights.  Just wondered if its a different kind of light or if there is something wrong.  Also, has anyone commented on the fact that we cant use apostrophes and other punctuation when sending these requests?  I wonder who could fix that?  Someone in IT maybe?  Ill ask. ', '---SELECT ONE---', '2006-08-30', '0000-00-00', 'We believe the ballast in that light is bad, it will have to be ordered, and replaced, I replaced the ceiling tile, the light cover is a different color, making it look yellow, and just avoid using apostraphes or even any punctuation if it gives you trouble. IT is aware of the problem. ');
